mapper.select:
SELECT ANY_VALUE(sku.id) AS id, sku.product_id, sku.group_activity_id, MIN(sku.active_price) AS active_price, ANY_VALUE(sku.quota_show) AS quota_show, ANY_VALUE(sku.quota) AS quota, ANY_VALUE(sku.sku_id) AS sku_id, ANY_VALUE(sku.group_status) AS group_status, ANY_VALUE(sku.activity_status) AS activity_status, ANY_VALUE(ac.group_name) AS group_name, ANY_VALUE(ac.group_status) AS activity_group_status, ANY_VALUE(ac.product_count) AS product_count, ANY_VALUE(ac.buy_count) AS buy_count, ANY_VALUE(ac.show_group) AS show_group, ANY_VALUE(ac.ficti_status) AS ficti_status, ANY_VALUE(ac.total_order_begin) AS total_order_begin, ANY_VALUE(ac.activity_status) AS activity_activity_status, ANY_VALUE(product.image) AS image_url, -- 从 eb_product 表中获取商品图片 ANY_VALUE(product.name) AS product_name, -- 从 eb_product 表中获取商品名称 ANY_VALUE(product.price) AS price -- 从 eb_product 表中获取商品售价 FROM eb_group_buy_activity_sku sku JOIN eb_group_buy_activity ac ON sku.group_activity_id = ac.id JOIN eb_product product ON sku.product_id = product.id INNER JOIN eb_product_attr_value pav ON sku.sku_id = pav.id WHERE ac.activity_status = 1 AND ac.group_status = 4 AND ac.is_del = 0 -- 开启的拼团活动 并且审核通过的 AND NOW() BETWEEN ac.start_time AND ac.end_time -- 符合活动时间的商品 AND product.is_del = 0 AND product.audit_status IN(0 ,2) -- 审核成功 和 免审商品
AND ac.show_group = #{showgroup} -- 根据条件查询开启凑团 凑团 0不可见，1可见
AND pav.is_show = 1 GROUP BY sku.product_id, sku.group_activity_id ORDER BY ANY_VALUE(ac.create_time) DESC, ANY_VALUE(ac.id) DESC , sku.product_id DESC LIMIT #{limit} OFFSET #{offset};

SELECT sku.* FROM eb_group_buy_activity_sku sku INNER JOIN eb_product p ON sku.product_id = p.id INNER JOIN eb_product_attr_value pav ON sku.sku_id = pav.id INNER JOIN eb_group_buy_activity ac ON sku.group_activity_id = ac.id WHERE sku.activity_status = 1 and sku.group_status = 4 AND p.is_del = 0 AND (p.audit_status = 2 OR p.audit_status = 0) -- 审核成功 和 免审商品
AND p.mer_id = #{merId}
AND pav.is_show = 1 AND ac.activity_status = 1 AND ac.group_status = 4 AND ac.is_del = 0 -- 开启的拼团活动 并且审核通过的

SELECT ANY_VALUE(sku.id) AS id, sku.product_id, sku.group_activity_id, MIN(sku.active_price) AS active_price, ANY_VALUE(sku.quota_show) AS quota_show, ANY_VALUE(sku.quota) AS quota, ANY_VALUE(sku.sku_id) AS sku_id, ANY_VALUE(sku.group_status) AS group_status, ANY_VALUE(sku.activity_status) AS activity_status, ANY_VALUE(ac.group_name) AS group_name, ANY_VALUE(ac.group_status) AS activity_group_status, ANY_VALUE(ac.product_count) AS product_count, ANY_VALUE(ac.buy_count) AS buy_count, ANY_VALUE(ac.show_group) AS show_group, ANY_VALUE(ac.ficti_status) AS ficti_status, ANY_VALUE(ac.total_order_begin) AS total_order_begin, ANY_VALUE(ac.activity_status) AS activity_activity_status, ANY_VALUE(product.image) AS image_url, -- 从 eb_product 表中获取商品图片 ANY_VALUE(product.name) AS product_name, -- 从 eb_product 表中获取商品名称 ANY_VALUE(product.price) AS price -- 从 eb_product 表中获取商品售价 FROM eb_group_buy_activity_sku sku JOIN eb_group_buy_activity ac ON sku.group_activity_id = ac.id JOIN eb_product product ON sku.product_id = product.id INNER JOIN eb_product_attr_value pav ON sku.sku_id = pav.id WHERE ac.activity_status = 1 AND ac.group_status = 4 AND ac.is_del = 0 -- 开启的拼团活动 并且审核通过的 AND NOW() BETWEEN ac.start_time AND ac.end_time -- 符合活动时间的商品 AND product.is_del = 0 AND product.audit_status IN (0,2) -- 审核成功 和 免审商品 AND pav.is_show = 1
AND product.mer_id = #{merId}
GROUP BY sku.product_id, sku.group_activity_id ORDER BY ANY_VALUE(ac.create_time) DESC, ANY_VALUE(ac.id) DESC , sku.product_id DESC
