mapper.select:
SELECT COUNT(*) FROM human_service_message 
WHERE session_id = #{sessionId} 
AND receiver_id = #{receiverId}
AND is_read = 0

mapper.update:
UPDATE human_service_message 
SET is_read = 1, read_time = NOW(), update_time = NOW()
WHERE session_id = #{sessionId} 
AND receiver_id = #{receiverId}
AND is_read = 0

mapper.select:
SELECT * FROM human_service_message 
WHERE receiver_id = #{receiverId} 
AND receiver_type = #{receiverType}
AND is_read = 0
ORDER BY create_time ASC
LIMIT 50
